def compute_accuracy_precision(clusters, labed_file):
    correct = 0
    wrong = 0
    goldstandard = [line for line in labed_file if line[len(line)-1] == '1']
    total_population = min(len(clusters), len(goldstandard))
    accuracies = []


    print(len(clusters))

    for pair in clusters:
        for result in labed_file:
            if(pair[0] == result.split('\t')[0].split('VAL')[1].split('COL')[0] and pair[1] == result.split('\t')[1].split('VAL')[1].split('COL')[0]):
                if(result[len(result)-1] == '1'):
                    correct+=1
                    break #do not need to continue
                else:
                    print(result)
                    wrong+=1
                    break #do not need to continue
                # print(result)

    precision = correct/len(clusters)

    print("Correct: " + str(correct))
    print("Wrong: " + str(wrong))
    print("Precision: " + str(precision))
    accuracies.append([precision])

    return accuracies, precision

def perform_evaluation(clusters, labed_file, results):
    print('========FAIRNESS========')

    R = fairness_metrics.compute_bias(clusters)
    results["Bias"].append(R)
    print('R-bias: ', R)
    print('========================')
    # print('TPRP: ', fairness_metrics.compute_TPRP(clusters_20, goldstandard, DATASET, 'fair'))
    ppvp = fairness_metrics.compute_PPVP(clusters, labed_file)
    results["PPVP"].append(ppvp)
    print('PPVP: ', ppvp)
    print('========================')
    tprp = fairness_metrics.compute_TPRP_top_k(clusters, labed_file, DATASET, 'm-fair')
    results["TPRP"].append(tprp)
    print('TPRP: ', tprp)
    print('========================')


    top_ks = [5, 10, 15, 20]


    for top_k in top_ks:
        clusters_sliced = clusters[:top_k]

        print('RESULTS TOP-' + str(len(clusters_sliced)))

        accuracies,precision = compute_accuracy_precision(clusters_sliced, labed_file)

        results["top-" + str(top_k)].append(precision)

        print("====================")

    # print(accuracies)


